function footerMenu(){let t=document.querySelectorAll(".footer__mobile-nav-title"),e=document.querySelectorAll(".footer__mobile-nav-list");t.forEach((t,a)=>{t.addEventListener("click",()=>{e[a].classList.toggle("footer__mobile-nav-list--active")})})}function tabs(t,e){e.forEach((a,o)=>{a.addEventListener("click",a=>{for(let o=0;o<e.length;o++)e[o].classList.remove("tabs-navigation__item--active"),a.target.classList.add("tabs-navigation__item--active"),t[o].classList.remove("tab--active");t[o].classList.add("tab--active")})})}function productCardTabs(){const t=document.querySelectorAll(".product-card-navigation__item");tabs(document.querySelector(".product-card__tab-body").querySelectorAll(".tab"),t)}footerMenu(),productCardTabs();