function footerMenu(){let e=document.querySelectorAll(".footer__mobile-nav-title"),t=document.querySelectorAll(".footer__mobile-nav-list");e.forEach((e,o)=>{e.addEventListener("click",()=>{t[o].classList.toggle("footer__mobile-nav-list--active")})})}function mobileMenu(){const e=document.querySelector(".header__mobile-menu-btn"),t=document.querySelector(".header__nav");let o=0;e.addEventListener("click",()=>{0===o?(t.style.display="block",o=1):(t.style.display="none",o=0)})}footerMenu(),mobileMenu();